// import React, { Fragment } from "react";
import { useState } from "react";
import AuthImage from "../../reusables/AuthImages";
import Button from "../../UI/Button";
import classes from "./styles/signUp.module.css"

const SignUp = () => {

    const initialValue = {
        email: '',
        userName: '',
        password: '',
    }

    const [data, setData] = useState(initialValue);
    const navigate = useNavigate

    const onChangleHandler = (e)=>{
        setData((prev)=>({
            ...prev,[e.target.name] : e.target.value
        }))
    }

    const onSumbitHandler = async (e)=>{
        e.preventDefault();
        const userDetails = {
            email: data.email,
            userName: data.userName,
            password: data.password
        }

         console.log(userDetails);

         const response = await axios.post(
           "http://localhost:8080/api/v1/user/register",
           userDetails
         );
         console.log(userDetails);
         if (response.status === 200) {
           console.log("BC res -->", response.data);
         }

         setData("");
         navigate("/login");
    }    

  return (
    <div className={classes.mainContainer}>
      <div>
        <AuthImage />
      </div>
      <div className={classes.formContainer}>
        <p className={classes.register}>REGISTER</p>
        <form action="" className={classes.form} onSubmit={onSumbitHandler}>
          <label htmlFor="email">
            Email <span>*</span>
          </label>
          <div>
            <input
              type="email"
              name="email"
              onChange={onChangleHandler}
              value={data.email}
              required
            />
          </div>
          <label htmlFor="username">
            Username <span>*</span>
          </label>
          <div>
            <input
              type="text"
              name="userName"
              onChange={onChangleHandler}
              value={data.userName}
              required
            />
          </div>
          <label htmlFor="password">
            Password <span>*</span>
          </label>
          <div>
            <input
              type="password"
              name="password"
              onChange={onChangleHandler}
              value={data.password}
              required
            />
          </div>
          <Button>Sign up</Button>
        </form>
        <div className={classes.forgottenPasswordLogin}>
          <p className={classes.login}>
            <a href="#">Login</a>
          </p>
          <p className={classes.login}>
            <a href="#">forgotten password</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
