import React, { useState} from "react";
import AuthImage from "../../reusables/AuthImages";
import Button from "../../UI/button/Button";
import axios from "axios";
import classes from "./styles/signUp.module.css"
import { Navigate, useNavigate } from "react-router-dom";
import Card from "../../UI/card/Card";
import NavigationButton from "../../UI/button/NavigationButton";

const SignUp = () => {

    const initialValue = {
        email: '',
        userName: '',
        password: '',
    }

    const [data, setData] = useState(initialValue);
    const [goToLogin, setGoToLogin] = useState(false)
    // const navigate = useNavigate()

    const onChangleHandler = (e)=>{
      setData((prev)=>({
          ...prev,[e.target.name] : e.target.value
      }))
    }

    const onClickHandler = () =>{
       setGoToLogin(true)

       if(goToLogin){
        return <Navigate to="/lo" />
       }
    }

    const onSumbitHandler = async (e)=>{
        e.preventDefault();
        const userDetails = {
            email: data.email,
            userName: data.userName,
            password: data.password
        }

         console.log(userDetails);

         const response = await axios.post(
           "http://localhost:8080/api/v1/user/register",
           userDetails
         );
         console.log(userDetails);
         if (response.status === 200) {
           console.log("BC res -->", response.data);
         }

         setData("");
         navigate("/login");
    }    

  return (
    <Card>
      <div className={classes.mainContainer}>
        <div>
          <AuthImage />
        </div>
        <div className={classes.formContainer}>
          <p className={classes.register}>REGISTER</p>
          <form action="" className={classes.form} onSubmit={onSumbitHandler}>
            <label htmlFor="email">
              Email <span>*</span>
            </label>
            <div>
              <input
                placeholder="semicolon's email"
                type="email"
                name="email"
                onChange={onChangleHandler}
                value={data.email}
                required
              />
            </div>
            <label htmlFor="username">
              Username <span>*</span>
            </label>
            <div>
              <input
                placeholder="wifi username"
                type="text"
                name="userName"
                onChange={onChangleHandler}
                value={data.userName}
                required
              />
            </div>
            <label htmlFor="password">
              Password <span>*</span>
            </label>
            <div>
              <input
                placeholder="wifi password"
                type="password"
                name="password"
                onChange={onChangleHandler}
                value={data.password}
                required
              />
            </div>
            <Button>Sign up</Button>
          </form>
          <div>
            <NavigationButton onClick={onClickHandler}>Login</NavigationButton>
          </div>
        </div>
      </div>
    </Card>
  );
};

export default SignUp;
