import React, { Fragment } from 'react';
import classes from "./styles/login.module.css"
import AuthImage from "../../reusables/AuthImages";
import Card from '../../UI/card/Card';
import Button from '../../UI/button/Button';
// import img from "../../../../src/assests/images/istockphoto-1421053049-612x612.jpg";

const Login =() => {

    // const myStyle={
    //     // backgroundImage: `url(${img})` ,
    //     height:'100vh',
    //     // marginTop:'-70px',
    //     // marginBottom: "-70px",
    //     margin: "-56px",
    //     fontSize:'50px',
    //     backgroundSize: 'cover',
    //     backgroundRepeat: 'no-repeat',
    // };

  return (
    <Card>
      <div className={classes.mainContainer}>
        <div>
          <AuthImage />
        </div>

        <div className={classes.formContainer}>
          <form action="" className={classes.form}>
            <label htmlFor="" style={{ marginTop: "30px" }}>
              Username <span>*</span>{" "}
            </label>
            <br></br>
            <input type="text" name="" className={classes.input} />
            <br></br>

            <label htmlFor="">Password: </label>
            <br></br>
            <input type="password" name="" className={classes.input} />
            <br></br>
            <br></br>
            <Button>Login</Button>
          </form>
          <div>
            <p className={classes.login}>
              <a href="#">Register</a>
            </p>
          </div>
        </div>
      </div>
    </Card>
  );
}


export default Login;
