      attendanceStatus: "P",
    },
    {
      serialNumber: "1",
      firstName: "chinedu",
      lastName: "ugbo",
      cohort: "13",
      date: "22/12/2023",
      attendanceStatus: "P",
    },
  ];

  const [currentPage, setCurrentPage] = useState(1)
  const recordsPerPage = 5;
  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = attendanceReport.slice(firstIndex, lastIndex);
  const npage = Math.ceil(attendanceReport.length / recordsPerPage)
  const numbers = [...Array(npage + 1).keys()].slice(1)

  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("")

  const startDateHandler = (e)=>{
    setStartDate(e.target.value)
  }

    const endDateHandler = (e) => {
      setEndDate(e.target.value);
    };

    const submitHandler = async (e) =>{
      e.preventDefault()

      dateDetails = {
        startDate: startDate,
        endDate: endDate
      }

       try {
      const response = await axios.post(
        "https://elitestracker-production.up.railway.app/api/v1/natives/genera",
        dateDetails
      );
    } catch (error) {
      setError(error.response.data.data);
      console.log(error.response.data.data)
    }
    }

  return (
    <div className={classes.main}>
      <NativeSideBar />
      <div className={classes.innerContainer}>
        <p>Generate Native's Attendance Report</p>
        <form action="" onSubmit={submitHandler} className={classes.formInput}>
          <input className={classes.input} onChange={startDateHandler} type="date" name="" id="" />
          <input className={classes.input} onChange={endDateHandler} type="date" name="" id="" />
          <Button className={classes.btn}> Generate</Button>
        </form>
        <table>
          <thead>
            <th>S/N</th>
            <th>Name</th>
            <th>Date</th>
          </thead>
          <tbody>
            {records.map((report, i) => (
              <tr key={i}>
                <td>{report.serialNumber}</td>
                <td>
                  {report.firstName} {report.lastName}
                </td>
                <td>{report.attendanceStatus}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <nav>
          <ul>
            <li>
              <a href="#" onClick={prePage}>Prev</a>
            </li>
            {numbers.map((n,i)=>(
              <li className={`pageItems $(currentPage === n ? 'active' : '')`} key={i}>
                <a href="#" onClick={()=> changeCPage(n)} >{n}</a>
              </li>
            ))}
            <li>
              <a href="#" onClick={nextPage}>Next</a>
            </li>
          </ul>
        </nav>
      </div>

      {/* <table className="view-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Name</th>
            <th>Result</th>
          </tr>
        </thead>
        <tbody>
          {viewlog.map((viewlog, index) => (
            <tr key={index}>
              <td>{viewlog.Date}</td>
              <td>{viewlog.Name}</td>
              <td>{viewlog.Result}</td>
            </tr>
          ))}
        </tbody>
      </table> */}
    </div>
  );

  function prePage() {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1) 
    }
  }

  function changeCPage(id) {
    setCurrentPage(id)
  }

  function nextPage() {
    if (currentPage !== npage) {
      setCurrentPage(currentPage + 1)
    }
  }
};

export default NativeGenerateAttendanceReport;
